script:
// PIM Dashboard Page Script
frappe.pages['pim-dashboard'].on_page_load = function(wrapper) {
	// Clear any existing content
	$(wrapper).find('.layout-main-section').empty();

	// Create container for React app
	var container = $('<div id="pim-dashboard-root" style="height: 100vh; width: 100%;"></div>');
	$(wrapper).find('.layout-main-section').append(container);

	// Add loading spinner
	container.html(`
		<div class="pim-loading" style="display: flex; justify-content: center; align-items: center; height: 100vh; font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;">
			<div class="pim-loading-spinner" style="border: 4px solid #f3f3f3; border-top: 4px solid #3498db; border-radius: 50%; width: 40px; height: 40px; animation: spin 2s linear infinite; margin-right: 16px;"></div>
			<div>Loading PIM Dashboard...</div>
		</div>
		<style>
			@keyframes spin {
				0% { transform: rotate(0deg); }
				100% { transform: rotate(360deg); }
			}
		</style>
	`);

	// Set global configuration for React app
	window.pimConfig = {
		user: frappe.session.user,
		csrfToken: frappe.csrf_token,
		apiBase: '/api/method/imperium_pim.api',
		basePath: '/app/pim-dashboard'
	};

	// Load React app assets
	loadPimAssets();
};

function loadPimAssets() {
	// Load CSS
	if (!document.querySelector('link[href*="pim-ui/assets/index"]')) {
		var cssLink = document.createElement('link');
		cssLink.rel = 'stylesheet';
		cssLink.href = '/assets/imperium_pim/pim-ui/assets/index.css';
		document.head.appendChild(cssLink);
	}

	// Load JS module
	if (!document.querySelector('script[src*="pim-ui/assets/index"]')) {
		var jsScript = document.createElement('script');
		jsScript.type = 'module';
		jsScript.src = '/assets/imperium_pim/pim-ui/assets/index.js';
		document.head.appendChild(jsScript);
	}
}

style:
/* PIM Dashboard Page Styles */
.page-container[data-page-route="pim-dashboard"] .layout-main-section {
	padding: 0 !important;
	margin: 0 !important;
	height: 100vh;
	overflow: hidden;
}

.page-container[data-page-route="pim-dashboard"] .page-head {
	display: none;
}

.page-container[data-page-route="pim-dashboard"] .layout-main {
	padding: 0 !important;
}

#pim-dashboard-root {
	height: 100vh !important;
	width: 100% !important;
	overflow: hidden;
}
